import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [gcd, setGcd] = useState('');
    const [result, setResult] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/calculate', { gcd: parseFloat(gcd) });
            setResult(response.data);
        } catch (error) {
            console.error("Error fetching data: ", error);
        }
    };

    return (
        <div>
            <h1>FFXIV GCD Calculator</h1>
            <form onSubmit={handleSubmit}>
                <input 
                    type="number" 
                    value={gcd} 
                    onChange={(e) => setGcd(e.target.value)} 
                    placeholder="Enter desired GCD" 
                    required 
                />
                <button type="submit">Calculate</button>
            </form>
            {result && (
                <div>
                    <h2>Results:</h2>
                    <p>Required Skill Speed: {result.skillspeed}</p>
                    <h3>Other Durations:</h3>
                    <pre>{JSON.stringify(result.otherGCDs, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default App;
