import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [gcd, setGcd] = useState('');
    const [lodestoneID, setLodestoneID] = useState(''); // New state for lodestoneID
    const [gcdResult, setGcdResult] = useState(null);
    const [characterResult, setCharacterResult] = useState(null); // New state for character details

    const handleGcdSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/calculate', { gcd: parseFloat(gcd) });
            setGcdResult(response.data);
        } catch (error) {
            console.error("Error fetching GCD data: ", error);
        }
    };

    const handleLodestoneSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/search', { lodestoneID });
            setCharacterResult(response.data); // Store the character result
        } catch (error) {
            console.error("Error fetching character data: ", error);
        }
    };

    return (
        <div>
            <h1>FFXIV GCD Calculator</h1>

            {/* GCD Calculation Form */}
            <form onSubmit={handleGcdSubmit}>
                <input 
                    type="number" 
                    value={gcd} 
                    onChange={(e) => setGcd(e.target.value)} 
                    placeholder="Enter desired GCD" 
                    required 
                />
                <button type="submit">Calculate</button>
            </form>

            {gcdResult && (
                <div>
                    <h2>GCD Results:</h2>
                    <p>Required Skill Speed: {gcdResult.skillspeed}</p>
                    <h3>Other Durations:</h3>
                    <pre>{JSON.stringify(gcdResult.otherGCDs, null, 2)}</pre>
                </div>
            )}

            {/* Lodestone ID Search Form */}
            <form onSubmit={handleLodestoneSubmit}>
                <input 
                    type="text" 
                    value={lodestoneID} 
                    onChange={(e) => setLodestoneID(e.target.value)} 
                    placeholder="Enter Lodestone ID" 
                    required 
                />
                <button type="submit">Search Character</button>
            </form>

            {characterResult && (
                <div>
                    <h2>Character Details:</h2>
                    <p>Name: {characterResult.name}</p>
                    <img src={characterResult.avatarURL} alt={`${characterResult.name}'s Avatar`} />
                    <img src={characterResult.portraitURL} alt={`${characterResult.name}'s Portrait`} />
                    <h3>Stats:</h3>
                    <pre>{JSON.stringify(characterResult.stats, null, 2)}</pre>
                    <h3>Equipment:</h3>
                    <pre>{JSON.stringify(characterResult.equipment, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default App;
